/* eslint-disable import/no-default-export */
declare module "*.svg" {
    const content: string;
    export default content;
}

declare module "*.png" {
    const value: string;
    export default value;
}

// Fonts
declare module "*.eot" {
    const value: string;
    export default value;
}

declare module "*.ttf" {
    const value: string;
    export default value;
}

declare module "*.woff" {
    const value: string;
    export default value;
}

// Images
declare module "*.gif" {
    const value: string;
    export default value;
}
